pragma solidity ^0.8.27;

import "solady/src/auth/Ownable.sol";
import "solady/src/utils/SSTORE2.sol";
import "./Sculpture.sol";

contract Essay is Sculpture, Ownable {

    address private pointer;
    string private t;
    string[] private u;

    constructor () {
        _initializeOwner(msg.sender);
    }

    function title() external view returns (string memory) {
        return t;
    }

    function authors() public pure returns (string[] memory authors_) {
        authors_ = new string[](1);
        authors_[0] = "Malte Rauch";
        return authors_;
    }

    function addresses() external view returns (address[] memory) {
        address[] memory _addresses = new address[](1);
        _addresses[0] = address(this);
        return _addresses;
    }

    function urls() external view returns (string[] memory) {
        return u;
    }

    function text() external view returns (string memory) {
        if (pointer == address(0)) {
            return "";
        }
        return string(SSTORE2.read(pointer));
    }

    function setTitle(string memory _title) external onlyOwner {
        t = _title;
    }

    function setUrls(string[] memory _urls) external onlyOwner {
        u = _urls;
    }

    function setText(string memory _text) external onlyOwner {
        pointer = SSTORE2.write(bytes(_text));
    }

    function html() external view returns (string memory html) {
        html = string.concat(
            "<h1>", t, "</h1>",
            "<h2>", authors()[0], "</h2>",
            "<div>", pointer == address(0) ? "" : string(SSTORE2.read(pointer)), "</div>");
    }
}","keccak256":"0xafda116ecb4fe003eb69a6c0cd7009b923eb0cf814b3285dbb9260c5349086bc","license":"UNLICENSED"},"contracts/FlowerGuestbook.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0;

import "solady/src/auth/Ownable.sol";

contract FlowerGuestbook is Ownable {

    uint256 private totalFlowers;
    uint256 private totalGuests;
    mapping(address => uint256) private flowersPlantedBy;
    mapping(uint256 => address) private flowerBy;
    mapping(uint256 => uint256) private flowerTimestamp;

    constructor () {
        _initializeOwner(msg.sender);
    }

    receive() external payable {
        if (msg.value < 0.01 ether) {
            return;
        }
        uint256 newFlowers = msg.value / 0.01 ether;
        if (flowersPlantedBy[msg.sender] == 0) {
            totalGuests++;
        }
        flowersPlantedBy[msg.sender] += newFlowers;
        totalFlowers += newFlowers;
        flowerBy[totalFlowers] = msg.sender;
        flowerTimestamp[totalFlowers] = block.timestamp;
    }

    function flower(uint256 flowerId) external view returns (address) {
        require(0 < flowerId && flowerId <= totalFlowers, "Index out of bounds");
        while (flowerId <= totalFlowers) {
            if (flowerBy[flowerId] != address(0)) {
                break;
            }
            flowerId++;
        }
        return flowerBy[flowerId];
    }

    function flowers() external view returns (uint256) {
        return totalFlowers;
    }

    function flowerPlantedAt(uint256 flowerId) external view returns (uint256) {
        require(0 < flowerId && flowerId <= totalFlowers, "Index out of bounds");
        while (flowerId <= totalFlowers) {
            if (flowerTimestamp[flowerId] != 0) {
                break;
            }
            flowerId++;
        }
        return flowerTimestamp[flowerId];
    }

    function flowersPlanted(address planter) external view returns (uint256) {
        return flowersPlantedBy[planter];
    }

    function guests() external view returns (uint256) {
        return totalGuests;
    }

    function withdraw(address _to) external onlyOwner() {
        (bool success, ) = _to.call{value: address(this).balance}("");
        require(success, "Failed to withdraw");
    }

    function flowerInfo(uint256 flowerId) external view returns (address planter, uint256 timestamp) {
        require(0 < flowerId && flowerId <= totalFlowers, "Index out of bounds");
        while (flowerId <= totalFlowers) {
            if (flowerBy[flowerId] != address(0)) {
                break;
            }
            flowerId++;
        }
        return (
            flowerBy[flowerId],
            flowerTimestamp[flowerId]
        );
    }
}","keccak256":"0x4b22ff246c1ff26e6ef97390602aacc1c8dfb6ca0b8bb00f3baeb32978180eac","license":"MIT"},"contracts/Garden.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
