{"compiler":{"version":"0.8.28+commit.7893614a"},"language":"Solidity","output":{"abi":[{"inputs":[{"internalType":"address","name":"garden","type":"address"},{"internalType":"address","name":"essayContract","type":"address"}],"name":"html","outputs":[{"internalType":"string","name":"html","type":"string"}],"stateMutability":"view","type":"function"}],"devdoc":{"kind":"dev","methods":{},"version":1},"userdoc":{"kind":"user","methods":{},"version":1}},"settings":{"compilationTarget":{"contracts/web/GardenEssay.sol":"GardenEssay"},"evmVersion":"paris","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":false,"runs":200},"remappings":[]},"sources":{"contracts/Essay.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

import "solady/src/auth/Ownable.sol";
import "solady/src/utils/SSTORE2.sol";
import "./Sculpture.sol";

contract Essay is Sculpture, Ownable {

    address private pointer;
    string private t;
    string private a = "maltefr";
    string[] private u;

    constructor () {
        _initializeOwner(msg.sender);
    }

    function title() external view returns (string memory) {
        return t;
    }

    function authors() public view returns (string[] memory authors_) {
        authors_ = new string[](1);
        authors_[0] = a;
        return authors_;
    }

    function addresses() external view returns (address[] memory) {
        address[] memory _addresses = new address[](1);
        _addresses[0] = address(this);
        return _addresses;
    }

    function urls() external view returns (string[] memory) {
        return u;
    }

    function text() external view returns (string memory) {
        if (pointer == address(0)) {
            return "";
        }
        return string(SSTORE2.read(pointer));
    }

    function setTitle(string memory _title) external onlyOwner {
        t = _title;
    }

    function setAuthor(string memory _author) external onlyOwner {
        a = _author;
    }

    function setUrls(string[] memory _urls) external onlyOwner {
        u = _urls;
    }

    function setText(string memory _text) external onlyOwner {
        pointer = SSTORE2.write(bytes(_text));
    }

    function html() external view returns (string memory html) {
        html = string.concat(
            "<h1>", t, "</h1>",
            "<br/>",
            "<h2>", authors()[0], "</h2>",
            "<br/>",
            "<div>", pointer == address(0) ? "" : string(SSTORE2.read(pointer)), "</div>");
    }
}","keccak256":"0x597fd885361ed1077be2a717da17062956db1594ee6b981582a2ab81a2d7881b","license":"UNLICENSED"},"contracts/IGarden.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

interface IGarden {
    function getSculptures() external view returns (address[] memory);
    function flower(uint256 flowerId) external view returns (address);
    function flowers() external view returns (uint256);
    function flowersPlanted(address planter) external view returns (uint256);
    function flowerPlantedAt(uint256 flowerId) external view returns (uint256);
    function flowerInfo(uint256 flowerId) external view returns (address planter, uint256 timestamp);
    function guests() external view returns (uint256);
}","keccak256":"0x575c62a2da70386f73efc2de331f30076d4bbee878af1c615f6d323b13ebc293","license":"UNLICENSED"},"contracts/Sculpture.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

interface Sculpture {

    function title() external view returns (string memory);

    function authors() external view returns (string[] memory);

    function addresses() external view returns (address[] memory);

    function urls() external view returns (string[] memory);

    function text() external view returns (string memory);

}
