pragma solidity >=0.8.0;

import "solady/src/utils/LibString.sol";
import "./GardenHTML.sol";
import "../IGarden.sol";
import "../Essay.sol";
import "../Sculpture.sol";

library GardenEssay {
    function html(address garden, address essayContract) public view returns (string memory html) {
        html = string.concat(html,
            '<div class="c essay"><div class="w"><div class="s g">',
                '<p><i>This text was published as part of the contract show: <a href="/" style="display: block">World Computer Sculpture Garden</a></i></p>',
                '<br/>',
                unicode'⚘',
                '<br/><br/><br/>',
                '<article>',
                Essay(essayContract).html(),
                '</article>',
                '<br/><br/>',
                unicode'⚘',
                '<br/><br/>',
                '<p><i>View the exhibition at: <a href="/" style="display: block">World Computer Sculpture Garden</a></i></p>',
                '<br/>',
            '</div></div></div>',
            unicode'<div class="f"><a href="/" style="text-decoration: none;">⚘</a></div>'
        );
        return GardenHTML.html(html, Sculpture(garden).title());
    }
}","keccak256":"0x0b7677347d68aab5bbde03068deb19447dc5f216a022a360de296a0bc33c2a9f","license":"UNLICENSED"},"contracts/web/GardenHTML.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.0;

library GardenHTML {
    function html(string memory body, string memory title) external pure returns (string memory) {
        string memory html_ = "<html>";
        html_ = string.concat(html_,
            '<head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0">',
            '<link rel="icon" type="image/svg+xml" href="data:image/svg+xml,%3Csvg xmlns=', unicode"'http://www.w3.org/2000/svg' viewBox='0 0 100 100'%3E%3Cstyle%3E text %7B fill: %23000; %7D @media (prefers-color-scheme: dark) %7B text %7B fill: %23fff; %7D %7D %3C/style%3E%3Ctext y='.9em' font-size='90'%3E ⚘ %3C/text%3E%3C/svg%3E%0A", '" />',
            '<title>', title ,'</title></head>',
            "<style>",
            '*, *::before, *::after { box-sizing: border-box; }',
            'html { -moz-text-size-adjust: none; -webkit-text-size-adjust: none; text-size-adjust: none; } html, body { margin: 0; padding: 0; } body { min-height: 100vh } html,body,pre { font-family: "Courier New", "Courier", monospace; font-size: 15px; }',
            'h1,h2,h3 { margin: 0; font-size: inherit; font-style: inherit; font-weight: inherit;}',
            ".c { max-width: 840px; margin: 0 auto; padding: 0 1.5em; box-sizing: border-box; }",
            ".c.essay { max-width: 620px; }",
            "a { color: inherit; text-decoration: underline; }",
            ".w { position: relative; min-height: 100vh; display: flex; align-items: center; padding: 10em 0; }",
            ".s { width: 100%; max-width: 840px; }",
            ".s:not(.g) a { max-width: 100%; display: inline-block; white-space: nowrap; overflow: hidden; text-overflow: ellipsis; }",
            ".t { max-width: 100%; overflow-x: auto; margin: 1em 0; }",
            ".i { margin: 0 0 5em; }",
            ".f { position: fixed; bottom: 1em; right: 1.3em; }",
            ".p { position: absolute; bottom: 2rem; left: 50%; transform: translateX(-50%); font-size: 1.4em; }",
            ".field { white-space: pre; margin: 0 auto; max-width: 100%; overflow-x: auto; }",
            ".field a { text-decoration: none; }",
            ".a { overflow: hidden; display: inline-block; max-width: 100%; text-overflow: ellipsis; vertical-align: middle; }"
            "</style>"
        );
        html_ = string.concat(html_, "<body>", body, "</body></html>");
        return html_;
    }
}","keccak256":"0x006f2e0c4a5013e1d68f7687960f640ae40946dd6bf04f8214794192b5298cfa","license":"UNLICENSED"},"solady/src/auth/Ownable.sol":{"content":"// SPDX-License-Identifier: MIT
